import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  AppBar,
  Toolbar,
  IconButton,
  Button,
} from '@mui/material';
import { ArrowBack, Assessment } from '@mui/icons-material';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import axios from 'axios';

function Metrics() {
  const navigate = useNavigate();
  const [metrics, setMetrics] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMetrics();
  }, []);

  const fetchMetrics = async () => {
    try {
      const response = await axios.get('/api/metrics/summary');
      setMetrics(response.data);
    } catch (error) {
      console.error('Error fetching metrics:', error);
    } finally {
      setLoading(false);
    }
  };

  const metricCards = [
    { title: 'BLEU Score', value: metrics?.bleu?.toFixed(2) || 'N/A', color: '#1976d2' },
    { title: 'ChrF Score', value: metrics?.chrf?.toFixed(2) || 'N/A', color: '#2e7d32' },
    { title: 'Style Similarity', value: metrics?.style_similarity_score?.toFixed(2) || 'N/A', color: '#ed6c02' },
    { title: 'Override Rate', value: metrics?.manual_override_rate?.toFixed(2) + '%' || 'N/A', color: '#d32f2f' },
    { title: 'Attribution Ratio', value: metrics?.attribution_ratio?.toFixed(2) + '%' || 'N/A', color: '#9c27b0' },
  ];

  return (
    <Box>
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" color="inherit" onClick={() => navigate('/')}>
            <ArrowBack />
          </IconButton>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Metrics Dashboard
          </Typography>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Grid container spacing={3}>
          {metricCards.map((card, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Card>
                <CardContent>
                  <Typography color="textSecondary" gutterBottom>
                    {card.title}
                  </Typography>
                  <Typography variant="h4" sx={{ color: card.color }}>
                    {card.value}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>

        <Paper sx={{ p: 3, mt: 4 }}>
          <Typography variant="h6" gutterBottom>
            Translation Quality Metrics
          </Typography>
          <Typography variant="body2" color="text.secondary">
            These metrics help track the quality and style preservation of translations.
          </Typography>
        </Paper>
      </Container>
    </Box>
  );
}

export default Metrics;
